  const [show, setShow] = useState(false);



  useEffect(() => {
    setShow(true);
  }, []);



  // other handle
  const handleLogin = (loginMode) => {
    switch (loginMode) {
      case 'phone':
        console.log(props.history.push('/login/phone'))
        props.history.push('/login/phone')
        break
      case 'email':

        props.history.push('/login/email')
        break
      case 'register':
        props.history.push('/login/register')
        break
      default:
    }
  }



  return (
    <CSSTransition
      in={show}
      timeout={300}
      appear={true}
      classNames="fly"
      unmountOnExit
    >
      <Container>
        <LoginIconWrapper>
          <img src={imgSrc} style={{ width: '100px', height: '100px', }} alt="" />
        </LoginIconWrapper>
        <LoginWrapper>
          <div className="login-bg"></div>
          <Button
            type="primary"
            shape="round"
            size="large"
            onClick={() => handleLogin('phone')}
          >
            手机号
          </Button>
          <Button
            type="primary"
            shape="round"
            size="large"
            onClick={() => handleLogin('email')}
          >
            网易云邮箱
          </Button>
          <Button
            type="primary"
            shape="round"
            size="large"
            onClick={() => handleLogin('qr')}
          >
            扫码
          </Button>

        </LoginWrapper>
        <LoginWrapper>
            <Button
              type="ghost"
              onClick={() => handleLogin('register')}
              shape="round"
              size="large"
              className="gap"
            >
              注册
            </Button>
        </LoginWrapper>
      </Container>
    </CSSTransition>
  )
