import React, { memo, useRef, useState, useEffect } from 'react'
import { Button } from 'antd'
import { Container,LoginWrapper, LoginIconWrapper } from './style'
import { CSSTransition } from 'react-transition-group';
import imgSrc from './netease-music.png'
//实现扫码，用户名，邮箱登录


/**
 * 登录页面(模态框)
 */
function Login(props) {

  const [show, setShow] = useState(false);

  useEffect(() => {
    setShow(true);
  }, []);


  return (
    <CSSTransition
      in={show}
      timeout={300}
      appear={true}
      classNames="fly"
      unmountOnExit
    >
      <Container>
        <LoginIconWrapper>
          <img src={imgSrc} style={{ width: '100px', height: '100px', }} alt="" />
        </LoginIconWrapper>
        <LoginWrapper>
          <div className="login-bg"></div>
          <Button
            type="primary"
            shape="round"
            size="large"
            onClick={() => props.history.push("/login/phone")}
          >
            手机号
          </Button>
          <Button
            type="primary"
            shape="round"
            size="large"
            onClick={() => props.history.push("/login/email")}
          >
            网易云邮箱
          </Button>
          <Button
            type="primary"
            shape="round"
            size="large"
            onClick={() => props.history.push("/login/qr")}
          >
            扫码
          </Button>

        </LoginWrapper>
        <LoginWrapper>
            <Button
              type="ghost"
              onClick={() => props.history.push("/resigter")}
              shape="round"
              size="large"
              className="gap"
            >
              注册
            </Button>
